import { Link } from 'react-router-dom';
import * as auth from '../auth.js';
import React from 'react';

export const Register = (props) => {
  props.handleText('Войти');

  const [formValue, setFormValue] = useState({
    email: '',
    password: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;

    setFormValue({
      ...formValue,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const { password, email } = formValue;
    auth.register(password, email);
  };

  return (
    <div className="sign-up">
      <div className="sign-up__container">
        <h1 className="sign-up__title">Регистрация</h1>
        <form className="sign-up__form">
          <input
            value={formValue.email}
            onChange
            placeholder="Email"
            className="sign-up__input"
            required
            type="email"
          ></input>
          <input
            value={formValue.password}
            placeholder="Пароль"
            className="sign-up__input"
            required
            type="password"
          ></input>
          <button
            onClick={handleSubmit}
            type="submit"
            className="sign-up__submit"
          >
            Зарегестрироваться
          </button>
        </form>
        <div className="sign-up__text-container">
          <p className="sign-up__info-text">Уже зарегестрированы?</p>
          <Link to="/sign-in" className="sign-up__link">
            Войти
          </Link>
        </div>
      </div>
    </div>
  );
};
