import { Link } from 'react-router-dom';
import * as auth from '../auth.js';

export const Register = (props) => {
  props.handleText('Войти');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (formValue.password === formValue.confirmPassword) {
      const { username, password, email } = formValue;
      duckAuth.register(username, password, email);
    }
  };

  return (
    <div className="sign-up">
      <div className="sign-up__container">
        <h1 className="sign-up__title">Регистрация</h1>
        <form className="sign-up__form">
          <input
            placeholder="Email"
            className="sign-up__input"
            required
            type="email"
          ></input>
          <input
            placeholder="Пароль"
            className="sign-up__input"
            required
            type="password"
          ></input>
          <button type="submit" className="sign-up__submit">
            Зарегестрироваться
          </button>
        </form>
        <div className="sign-up__text-container">
          <p className="sign-up__info-text">Уже зарегестрированы?</p>
          <Link to="/sign-in" className="sign-up__link">
            Войти
          </Link>
        </div>
      </div>
    </div>
  );
};
